var buildUrl = function(serverUrl, api, token, psize ,data){
	var url = serverUrl + '/api/' + api + '/?token=' + token;
	if(psize )
		url = url + '&psize=' + psize;
    if(data){
        url = url + '&sId=' + data;
    }
	return url;
}

